import React from 'react'
import 'boxicons';
import {default as api} from '../reduxstore/apiSlice'


function History() {
  const {data, isFetching, isSuccess, isError} = api.useGetLabelsQuery()
  let Transactions;

  if(isFetching){
    Transactions = <div>Fetching</div>
  }else if(isSuccess){
    Transactions = data.map((v,i)=>
      <Transaction key={i} category={v} handler={handlerClick}></Transaction>
    )
  }else if(isError){
    Transactions = <div>Error</div>
  }

  const handlerClick = (e) => {
    console.log(e.target);
    
  }

  return (
    <div className='flex flex-col py-6 gap-3'>
        <h1 className="py-4 text-md font-bold text-xl">History</h1>
        {Transactions}
    </div>
  )
}

export default History

function Transaction({category}) {
    if (!category) return null;
    return (
        <div className="item flex justify-center bg-gray-50 py-2 rounded-r" style={{borderRight:`8px solid ${category.color ?? '#e5e5e5'}`}}>
            <button className='px-3'> <box-icon color={`${category.color ?? '#e5e5e5'}`} size='18px' name='trash'></box-icon></button>
            <span className='block w-full'>{category.name ?? ''}</span>
        </div>
    )
}
